import React, { useState } from "react";
import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
} from "react-native";

// 페이지 import
import LoginScreen from "./LoginScreen";

export default function App() {
  const [selectedTab, setSelectedTab] = useState("Home");

  return (
    <View style={styles.container}>
      <LoginScreen />

      <View style={styles.tabBar}>
        <TouchableOpacity
          style={[
            styles.tabItem,
            selectedTab === "Home" && styles.tabItemSelected,
          ]}
          onPress={() => handleTabPress("Home")}
        >
          <Text style={styles.tabTitle}>Home</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.tabItem,
            selectedTab === "Profile" && styles.tabItemSelected,
          ]}
          onPress={() => handleTabPress("Profile")}
        >
          <Text style={styles.tabTitle}>Profile</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.tabItem,
            selectedTab === "Settings" && styles.tabItemSelected,
          ]}
          onPress={() => handleTabPress("Settings")}
        >
          <Text style={styles.tabTitle}>Settings</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[
            styles.tabItem,
            selectedTab === "Notifications" && styles.tabItemSelected,
          ]}
          onPress={() => handleTabPress("Notifications")}
        >
          <Text style={styles.tabTitle}>Notifications</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.tabContent}>
        {selectedTab === "Home" && <Text>Home screen</Text>}
        {selectedTab === "Profile" && <Text>Profile screen</Text>}
        {selectedTab === "Settings" && <Text>Settings screen</Text>}
        {selectedTab === "Notifications" && <Text>Notifications screen</Text>}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  tabBar: {
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    height: 50,
    backgroundColor: "#f2f2f2",
  },
  tabItem: {
    flex: 1,
    alignItems: "center",
    padding: 10,
  },
  tabItemSelected: {
    borderBottomWidth: 2,
    borderBottomColor: "#333",
  },
  tabTitle: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#333",
  },
  tabContent: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
